function loadTranslations(lang = "en") {
  if (!translations[lang]) lang = "en"; // fallback to English

  document.querySelectorAll("[data-translate]").forEach(el => {
    const key = el.getAttribute("data-translate");
    const translation = translations[lang][key];

    if (translation) {
      // If it's a textarea or input, set placeholder
      if (el.tagName === "TEXTAREA" || el.tagName === "INPUT") {
        el.placeholder = translation;
      } else {
        el.textContent = translation;
      }
    }
  });

  localStorage.setItem("selectedLang", lang);
}

document.addEventListener("DOMContentLoaded", () => {
  const savedLang = localStorage.getItem("selectedLang") || "en";
  loadTranslations(savedLang);

  const langSelect = document.getElementById("lang-select");
  if (langSelect) {
    langSelect.value = savedLang;
    langSelect.addEventListener("change", e => {
      const selectedLang = e.target.value;
      loadTranslations(selectedLang);
    });
  }
});

const langSwitcher = document.getElementById("lang-switcher");
const selectedLang = langSwitcher.querySelector(".selected-lang");
const options = langSwitcher.querySelector(".lang-options");

// Load saved language from localStorage or default to "en"
let savedLang = localStorage.getItem("selectedLang") || "en";
selectedLang.textContent = savedLang.toUpperCase() + " ‚ñº";
loadTranslations(savedLang);

// Toggle dropdown open/close
selectedLang.onclick = () => {
  langSwitcher.classList.toggle("open");
};

// Handle language change
options.querySelectorAll("div").forEach(opt => {
  opt.onclick = () => {
    const lang = opt.getAttribute("data-lang");
    selectedLang.textContent = lang.toUpperCase() + " ‚ñº";
    langSwitcher.classList.remove("open");

    // Save language for persistence
    localStorage.setItem("selectedLang", lang);

    // Load translations immediately
    loadTranslations(lang);
  };
});

// Close dropdown when clicking outside
document.addEventListener("click", e => {
  if (!langSwitcher.contains(e.target)) langSwitcher.classList.remove("open");
});
